insert into news values(448,'Сбор на "Монолит"','<!--IMG1-->
<!--IMG1--> Пока "Вега" по пенальти одолела в финале КН-10 "Волынь", пока мы провели уже вторую тренировку, на которой было 14 человек, пока все усиленно качают недавно вышедшую "Мафию 2" - самое время напомнить о сборе на завтрашний выездной матч с "<b>Монолитом</b>". Ничего оригинального - в <b>17:00</b> <b>у танков</b>. Кто поедет сразу туда (<b>стадион "Маяк"</b>) - обязательно отпишите. Форма синяя, сбор 10 грн, не опаздывать. О явке/неявке - мне в ЛС.<br>До встречи!<br>',1,373,'2010-08-25');
